package Day1;

public class while1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
        
		//first face of while loop while performing like for
		int i = 1;   //counter declaration and initialization 
		while (i <= 10) {   //condition
			System.out.println("5*" + i + " = " + (i * 5));
			i++;      //increment
		}
	}
}
